import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';

function Square(props) {
    //make square on board
    return (
        <div className="square" onClick={props.onClick}>{props.value}</div>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //make an empty square array
            squares: Array(9).fill(null),
            //X will move first
            xNext : true,
        };
    }
    //add click event, its will appear X when click
    handleClick(i) {
        const squares = this.state.squares.slice();
        //check next move is X or O
        squares[i] = this.state.xNext?'X':'O';
        this.setState({ 
            squares: squares ,
            //set check function
            xNext: !this.state.xNext,

        });
    }
    renderSquare(i) {
        //render square
        return (
            <Square
                value={this.state.squares[i]}
                onClick={() => this.handleClick(i)}
            />);
    }
    render() {
        //render board
        const status = 'Next move: '+(this.state.xNext?'X':'O');
        return (
            <div className="board">
                <h4 className="status">{status}</h4>
                <div className="row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}
function findWinner(){
