import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';

class Square extends React.Component {
    //make square on board
    constructor(props){
        super(props);
        this.state = {
            //set value for each square
            value : null,
        };
    }
    render() {
        return (
            //add click event, its will appear X when click
            <div className="square" onClick={()=>this.setState({value:'X'})}>{this.state.value}</div>
        );
    }
}

