        //render square
        return <Square value={i}/>;
    }
    render(){
        //render board
        const status = 'Next: X';
        return(
            <div className="container">
                <h4 className="status">{status}</h4>
                <div className="row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}
class Game extends React.Component {
    render() { 
        return ( 
            <Board/>
         );
    }
}
 
export default Game;
