import React, { Component, useState } from 'react';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';


//=============Render Game==============================
function Game() {
    return (
        <div>
            <Square/>
        </div>
    );
}
//=========================================================


export default Game;
/* 
   Make square
   -value {props}
   -onclick event
*/
function Square(){
    return <button className="square btn border border-dark" onClick={props.onClick}>{p.value}</button>
}
//Make board
function Board(){
    const [square,setSquare] = useState(Array(9).fill(null));
    const [Xnext,setX] = useState(true);
    //add handle click
    const handleClick = index =>{
        const squares =[...square];
        //check square is fill or not
        if(squares[index]) return;
        //add X || O 
        squares[index] = Xnext?"X":"O";
        setSquare[squares];
        setX[!Xnext];
        return 
        <div>
            <div></div>
            <div></div>
            <div></div>
            <div></div>
        </div>
    }
}
