import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import 'bootstrap/dist/css/bootstrap.css';

function Square(props) {
    //make square on board
    return (
        <div className="square" onClick={props.onClick}>{props.value}</div>
    );
}

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            //make an empty square array
            squares: Array(9).fill(null),
            //X will move first
            xNext : true,
        };
    }
    //add click event, its will appear X when click
    handleClick(i) {
        const squares = this.state.squares.slice();
        //check next move is X or O
        squares[i] = this.state.xNext?'X':'O';
        this.setState({ 
            squares: squares ,
            //set check function
            xNext: !this.state.xNext,

        });
    }
    renderSquare(i) {
        //render square
        return (
            <Square
                value={this.state.squares[i]}
                onClick={() => this.handleClick(i)}
            />);
    }
    render() {
        const winner = findWinner(this.state.squares);
        let status;
        //check winner or keep move
        status = winner?('Winner: '+winner):'Next move: '+(this.state.xNext?'X':'O');
        //render board
        return (
            <div className="board">
                <h4 className="status">{status}</h4>
                <div className="row">
                    {this.renderSquare(0)}
                    {this.renderSquare(1)}
                    {this.renderSquare(2)}
                </div>
                <div className="row">
                    {this.renderSquare(3)}
                    {this.renderSquare(4)}
                    {this.renderSquare(5)}
                </div>
                <div className="row">
                    {this.renderSquare(6)}
                    {this.renderSquare(7)}
                    {this.renderSquare(8)}
                </div>
            </div>
        );
    }
}
function findWinner(squares){
    const lines = [
        [0,1,2],[3,4,5],[6,7,8],[0,3,6],[0,4,8],[1,4,7],[2,4,6],[2,5,8],
    ];
    for(let x of lines){
        const [a,b,c] = x;
        //che
        if(squares[a]&&squares[a]===squares[b]&&squares[a]===squares[c]){
            return squares[a];
        }
    }
    return null;
}

//=======================================================================

class Game extends React.Component {
    render() {
        return (
            <Board />
        );
    }
}

export default Game;
ReactDOM.render(<Game />, document.getElementById('root'));
